import styled from "styled-components";

export const Label = styled.label`
    font-size: 1.2rem;

    user-select: none;

    //text-transform: uppercase;

    color: ${props => props.color};

    &:hover {
        color: ${props => props.hoverColor || props.color};
    }
`;
