import React from 'react'
import * as C from './styles'

// Controlar as dimensão da input com font-size'css'
const Input = ({ type, placeholder, value, onChange, fontSize, height, bg, text }) => {
  const inputStyle = {
    fontSize: fontSize,
    height: height,
    background: bg
  }
  return (
    <C.Input
      type={type}
      placeholder={placeholder}
      value={value}
      onChange={onChange}
      style={inputStyle}
    >
      {text}
    </C.Input>
  )
}

export default Input
