import React, { useState } from 'react';
import Calendario from '../../components/Calendario';
import * as C from './styles';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser, faCalendarDays, faWallet, faClock } from '@fortawesome/free-solid-svg-icons';
import AddTask from '../../components/AddTask';
import { DataProvider, useDataContext } from '../../context/DataContext';

const Home = () => {

    const [selectedIcon, setSelectedIcon] = useState(false);
    const [showAddTaskContainer, setShowAddTaskContainer] = useState(false); // State do cotainer de cadastro de atividades

    // Pegando useDataContext do contexto
    const { dataSelecionada } = useDataContext();

    const toogleCalendarIcon = () => {
        setSelectedIcon(!selectedIcon);
    }

    // Toggle de controle da da box Adicionar tarefa
    const toggleAddTask = () => {
        //alert('Função adicionar tarefa');
        setShowAddTaskContainer(!showAddTaskContainer);
    };

    return (
        <C.Container>
            <C.Navbar>
                <C.NavbarContent>
                    <C.IconContentLogo>
                        LG
                    </C.IconContentLogo>
                    <C.IconGroup>
                        <C.IconContent
                            selected={selectedIcon}
                        >
                            <FontAwesomeIcon icon={faCalendarDays} onClick={toogleCalendarIcon} />
                        </C.IconContent>
                        <C.IconContent>
                            <FontAwesomeIcon icon={faWallet} />
                        </C.IconContent>
                        <C.IconContent>
                            <FontAwesomeIcon icon={faClock} />
                        </C.IconContent>
                    </C.IconGroup>
                    <C.IconContent>
                        <FontAwesomeIcon icon={faUser} />
                    </C.IconContent>
                </C.NavbarContent>
            </C.Navbar>
            <C.CalendarContainer>
                <DataProvider>
                    <C.CalendarContent>
                        <C.AddTaskContent>
                            <C.Button position='left' border='#fb6e66' bg='#fb6e66' color='#fff' fs='24px' onClick={dataSelecionada} >+</C.Button>
                        </C.AddTaskContent>
                        <Calendario />
                        <C.MessageContent>
                            <C.Label>
                                Texto aqui
                            </C.Label>
                        </C.MessageContent>
                    </C.CalendarContent>
                    <C.DateInfoContent>
                        {/* Metodo utilizado para montar components, se showAddTaskContainer for true, <C.AddContainer> sera montado, caso não for, ele sera desmontado */}
                        {showAddTaskContainer && (
                            <AddTask />
                        )}
                    </C.DateInfoContent>
                </DataProvider>
            </C.CalendarContainer>
        </C.Container >
    )
}

export default Home
