import React, { useEffect, useState } from 'react';
import * as C from './styles';
import { useCalendarContext } from '../../context/DataContext';
import Axios from "axios";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTrash, faTurnUp, faPen } from '@fortawesome/free-solid-svg-icons';
import Input from '../Input';
import Button from '../Button';

const AddTask = () => {
    // Pegando useCalendarContext do contexto
    const { dataSelecionada, isTaskInfoVisible, showWeeklyTask, listDataSelecionada, setListData } = useCalendarContext();

    // State para guardar as informações das input
    const [values, setValues] = useState();


    const handleClickButton = () => {
        // Formatação da data para o DB
        const { dia, mes, ano } = dataSelecionada;
        const dataFormatada = ano + "/" + mes + "/" + dia;
        //console.log("Data: ", dataSelecionada);

        //console.log(values);
    }

    var dataString = null, titleString = null, disciplinaString = null, tipoString = null, observacaoString = null;
    useEffect(() => {
        if (listDataSelecionada) {
            console.log();
            dataString = listDataSelecionada[0].eve_dataHora;

            titleString = listDataSelecionada[0].eve_titulo;

            disciplinaString = listDataSelecionada[0].dis_id;

            tipoString = listDataSelecionada[0].cat_id;

            observacaoString = listDataSelecionada[0].eve_descricao;
        } else {
            // Converter a data e hora para o formato padrão Brasil
            // const dataHora = new Date(dataSelecionada);
            // const data = new Date(dataHora.getFullYear(), dataHora.getMonth(), dataHora.getDate());

            // const dia = data.getDate();
            // const mes = data.getMonth() + 1;
            // const ano = data.getFullYear();

            dataString = dataSelecionada;
        }
    }, []);

    // Verificar se dataSelecionado é um Array


    const handleClickExit = () => {
        showWeeklyTask();
        setListData(null);
    }

    // ---------- LOG -----------
    //console.log("Data selecionada: ", dataString);
    //console.log("Data selecionada: ", dataSelecionada);

    return (
        <>
            {isTaskInfoVisible && (
                <C.AddContainer>
                    <C.TopAddContainer>
                        <C.TopIconsContent>
                            <C.IconsContent>
                                <FontAwesomeIcon icon={faTurnUp} rotation={270} onClick={handleClickExit} />
                            </C.IconsContent>
                            <C.IconsContent>
                                <FontAwesomeIcon icon={faPen} />
                            </C.IconsContent>
                        </C.TopIconsContent>
                        <C.TopAddContent>
                            <C.DefaultA onClick={null}></C.DefaultA>
                            <C.Title color='#fff'>Resumo da data</C.Title>
                            <C.DefaultA onClick={null}></C.DefaultA>
                        </C.TopAddContent>
                    </C.TopAddContainer>
                    <C.MainAddCointainer>
                        <C.LabelContent>
                            <C.Label>{titleString}</C.Label>
                        </C.LabelContent>
                        <C.LabelContent>
                            <C.Label>{disciplinaString}</C.Label>
                        </C.LabelContent>
                        <C.LabelContent>
                            <C.Label>{tipoString}</C.Label>
                        </C.LabelContent>
                        <C.LabelContent>
                            <C.Label>{dataString}</C.Label>
                        </C.LabelContent>
                        <C.LabelContent>
                            <C.Label>{observacaoString}</C.Label>
                        </C.LabelContent>
                        <C.IconsContent>
                            <FontAwesomeIcon icon={faTrash} />
                        </C.IconsContent>
                    </C.MainAddCointainer>
                </C.AddContainer>
            )}
        </>
    )
}

export default AddTask
