import React from 'react';
import * as C from './styles';

const Label = ({ children, color = '#0F4C75', hvColor }) => {
    const LabelStyle = {
        color: color,
    }
    if (hvColor) {
        LabelStyle['&:hover'] = {
            color: hvColor,
        };
    }
    return (
        <C.Label
            style={LabelStyle}
        >
            {children}
        </C.Label>
    )
}

export default Label;
