import React, { useState } from 'react';
import * as C from './styles';
import { useDataContext } from '../../context/DataContext';
import Axios from "axios";
import Input from '../Input';
import Button from '../Button';

const AddTask = () => {
    // Pegando useDataContext do contexto
    const { dataSelecionada, diaAtual } = useDataContext();

    // State para guardar as informações das input
    const [values, setValues] = useState();

    const handleChangeValues = (value) => {
        //console.log(value.target.value);
        setValues(prevValue => ({
            ...prevValue,
            [value.target.name]: value.target.value,
        }));
    }

    const handleClickButton = () => {
        // Formatação da data para o DB
        const { dia, mes, ano } = dataSelecionada;
        const dataFormatado = new Date(ano, mes - 1, dia);
        console.log("Data: ", dataFormatado);

        Axios.post("http://localhost:3001/register", {
            titulo: values.titulo,
            disciplina: values.disciplina,
            tipo: values.tipo,
            data: values.data,
            observacao: values.observacao
        }).then((response) => {
            console.log(response);
        });
        console.log(values);
    }

    if (dataSelecionada) {
        //values.data = dataSelecionada;
        console.log(dataSelecionada);
        // Criando as const para manipular a data
        const { dia, mes, ano } = dataSelecionada;
        // O mês começa do zero (janeiro é 0)
        const novaData = new Date(ano, mes - 1, dia);
        // Convertendo a data para uma string no formato desejado (dd/mm/yyyy)
        var dataString = novaData.toLocaleDateString('pt-BR');
    } else
        var dataString = new Date().toLocaleDateString('pt-BR');

    // ---------- LOG -----------
    //console.log("Data selecionada: ", dataString);
    //console.log("Data selecionada: ", dataSelecionada);

    return (
        <C.AddContainer>
            <C.TopAddContainer>
                <C.TopAddContent>
                    <C.DefaultA onClick={null}></C.DefaultA>
                    <C.Title color='#fff'>REGISTRAR</C.Title>
                    <C.DefaultA onClick={null}></C.DefaultA>
                </C.TopAddContent>
            </C.TopAddContainer>
            <C.MainAddCointainer>
                <C.InputContent>
                    <C.AuxDiv>
                        <C.Label>título:</C.Label>
                    </C.AuxDiv>
                    <Input type='text' bg='#fff' name='titulo' onChange={handleChangeValues} ></Input>
                </C.InputContent>
                <C.InputContent>
                    <C.AuxDiv>
                        <C.Label>disciplina:</C.Label>
                    </C.AuxDiv>
                    <Input type='text' bg='#fff' name='disciplina' onChange={handleChangeValues} ></Input>
                </C.InputContent>
                <C.InputContent>
                    <C.AuxDiv>
                        <C.Label>tipo:</C.Label>
                    </C.AuxDiv>
                    <C.Select name='tipo' onChange={handleChangeValues}>
                        <C.Option value='atividade' >ATIVIDADE</C.Option>
                        <C.Option value='avaliação'>AVALIAÇÃO</C.Option>
                        <C.Option value='trabalho'>TRABALHO</C.Option>
                    </C.Select>
                </C.InputContent>
                <C.InputContent>
                    <C.AuxDiv>
                        <C.Label>data:</C.Label>
                    </C.AuxDiv>
                    <Input type='text' bg='#fff' name='data' onChange={handleChangeValues} value={dataString}></Input>
                </C.InputContent>
                <C.InputContent>
                    <C.AuxDiv>
                        <C.Label>observação:</C.Label>
                    </C.AuxDiv>
                    <Input type='text' bg='#fff' name='observacao' onChange={handleChangeValues} ></Input>
                </C.InputContent>
                <Button text='SALVAR' onClick={() => handleClickButton()}></Button>
            </C.MainAddCointainer>
        </C.AddContainer>
    )
}

export default AddTask
