import React from 'react';
import { useCalendarContext } from '../../context/DataContext';

const TaskWeekly = () => {
    // Controle de visibilidade do component
    const { isTaskAddVisible, isTaskInfoVisible } = useCalendarContext();

    return (
        <>
            {!isTaskAddVisible && !isTaskInfoVisible && (
                <div>
                    Atividades da semana
                </div>
            )}
        </>
    )
}

export default TaskWeekly
