import styled from "styled-components";

export const weeklyContainer = styled.div`
    width: 100%;
    height: 100%;

    display: flex;
    flex-direction: row;
    align-items: center;
    justify-content: space-between;

    border: 1px solid red;
`;

export const buttonContainer = styled.div`
    width: min-content;
    height: min-content;

    border: 1px solid green;
`;
