                <C.LabelContent>
                    <C.Label>{ }</C.Label>
                </C.LabelContent>
                <C.LabelContent>
                    <C.Label>{ }</C.Label>
                </C.LabelContent>
                <C.LabelContent>
                    <C.Label>{ }</C.Label>
                </C.LabelContent>
            </C.MainContainer>

        </C.WeeklyContainer>
    )
}

export default TaskWeekly
