import styled from "styled-components";

export const Title = styled.h2`
    font-size: 22px;
    text-transform: uppercase;

    color: #1B262C;

    ${({ color }) => color && `color: ${color};`}
    //border: 1px solid blue;

    height: min-content;
    text-align: center;
    
    color: black;
`;
