import React, { useState } from 'react';
import { useCalendarContext } from '../../context/DataContext';

const TaskWeekly = (items) => {
    const [currentIndex, setCurrentIndex] = useState(0);
    const goToPrevious = () => {
        setCurrentIndex((prevIndex) => (prevIndex > 0 ? prevIndex - 1 : 0));
    };

    const goToNext = () => {
        setCurrentIndex((prevIndex) =>
            prevIndex < items.length - 1 ? prevIndex + 1 : prevIndex
        );
    };

    return (
        <>
            <div className="carousel">
                {items.length > 1 && (
                    <>
                        <button onClick={goToPrevious} disabled={currentIndex === 0}>
                            Anterior
                        </button>
                        <button onClick={goToNext} disabled={currentIndex === items.length - 1}>
                            Pr√≥ximo
                        </button>
                    </>
                )}
                <div className="carousel-items">
                    {/* Renderizar o item atual */}
                    <div>{items[currentIndex]}</div>
                </div>
            </div>
        </>
    )
}

export default TaskWeekly
