import React, { useState } from 'react';
import * as C from './styles';
import { useCalendarContext } from '../../context/DataContext';
import Axios from "axios";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBell, faTurnUp } from '@fortawesome/free-solid-svg-icons';
import Input from '../Input';
import Button from '../Button';

const AddTask = () => {
    // Pegando useCalendarContext do contexto
    const { dataSelecionada, isTaskInfoVisible, closeTaskInfo } = useCalendarContext();

    // State para guardar as informações das input
    const [values, setValues] = useState();


    const handleClickButton = () => {
        // Formatação da data para o DB
        const { dia, mes, ano } = dataSelecionada;
        const dataFormatada = ano + "/" + mes + "/" + dia;
        //console.log("Data: ", dataSelecionada);

        //console.log(values);
    }

    // Verificar se dataSelecionado é um Array
    if (Array.isArray(dataSelecionada)) {
        console.log("isArray: ", dataSelecionada);
        const dataHora = new Date(dataSelecionada[0].eve_dataHora);
        // Convertendo a string para um objeto Date
        // Extraindo apenas a parte da data
        const data = new Date(dataHora.getFullYear(), dataHora.getMonth(), dataHora.getDate());

        const dia = data.getDate();
        const mes = data.getMonth() + 1;
        const ano = data.getFullYear();

        var dataString = `${dia}/${mes}/${ano}`;

        var titleString = dataSelecionada[0].eve_titulo;

        var disciplinaString = dataSelecionada[0].dis_id;

        var tipoString = dataSelecionada[0].cat_id;

        var observacaoString = dataSelecionada[0].eve_descricao;



        //console.log(dataString);

    }

    const handleClickExit = () => {
        closeTaskInfo();
    }

    // ---------- LOG -----------
    //console.log("Data selecionada: ", dataString);
    //console.log("Data selecionada: ", dataSelecionada);

    return (
        <>
            {isTaskInfoVisible && (
                <C.AddContainer>
                    <C.TopAddContainer>
                        <C.TopIconsContent>
                            <C.IconsContent>
                                <FontAwesomeIcon icon={faTurnUp} rotation={270} onClick={handleClickExit} />
                            </C.IconsContent>
                        </C.TopIconsContent>
                        <C.TopAddContent>
                            <C.DefaultA onClick={null}></C.DefaultA>
                            <C.Title color='#fff'>Resumo da data</C.Title>
                            <C.DefaultA onClick={null}></C.DefaultA>
                        </C.TopAddContent>
                    </C.TopAddContainer>
                    <C.MainAddCointainer>
                        <C.LabelContent>
                            <C.Label>{titleString}</C.Label>
                        </C.LabelContent>
                        <C.LabelContent>
                            <C.Label>{disciplinaString}</C.Label>
                        </C.LabelContent>
                        <C.LabelContent>
                            <C.Label>{tipoString}</C.Label>
                        </C.LabelContent>
                        <C.LabelContent>
                            <C.Label>{dataString}</C.Label>
                        </C.LabelContent>
                        <C.LabelContent>
                            <C.Label>{observacaoString}</C.Label>
                        </C.LabelContent>
                        <Button text='SALVAR' onClick={() => handleClickButton()}></Button>
                    </C.MainAddCointainer>
                </C.AddContainer>
            )}
        </>
    )
}

export default AddTask
