import React from 'react';
import Calendario from '../../components/Calendario';
import * as C from './styles';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser, faCalendarDays, faWallet, faClock } from '@fortawesome/free-solid-svg-icons';

const Home = () => {

    const selectedIcon = false;
    const toogleCalendarIcon = () => {

    }

    return (
        <C.Container>
            <C.Navbar>
                <C.NavbarContent>
                    <C.IconContentLogo>
                        LG
                    </C.IconContentLogo>
                    <C.IconGroup>
                        <C.IconContent
                            style={{
                                // Data atual
                                ...dia !== null && isDiaAtual(dia) ? { background: currentDateColor, border: '1px solid #fff' } : {},
                                // Controle de cores de acordo com a quantidade de atv cadastradas 
                                ...colorControl(dia, mesAtual, anoAtual),
                                // Data selecionada
                                ...(dataSelecionada && dia === dataSelecionada.dia && mesAtual === dataSelecionada.mes && anoAtual === dataSelecionada.ano
                                    ? { background: selectDateColor, border: '1px solid #fff' } : {})
                            }}>
                        >
                            <FontAwesomeIcon icon={faCalendarDays} onClick={toogleCalendarIcon} />
                        </C.IconContent>
                        <C.IconContent>
                            <FontAwesomeIcon icon={faWallet} />
                        </C.IconContent>
                        <C.IconContent>
                            <FontAwesomeIcon icon={faClock} />
                        </C.IconContent>
                    </C.IconGroup>
                    <C.IconContent>
                        <FontAwesomeIcon icon={faUser} />
                    </C.IconContent>
                </C.NavbarContent>
            </C.Navbar>
            <C.CalendarContainer>
                <C.CalendarContent>
                    <Calendario />
                </C.CalendarContent>
                <C.DateInfoContent>
                    Informações do calendario
                </C.DateInfoContent>
            </C.CalendarContainer>
        </C.Container>
    )
}

export default Home
