import styled from "styled-components";


export const Button = styled.button`
    width: 65%;
    //min-width: 15rem; 

    padding: 12px 0;
    cursor: pointer;

    background-color: #F74C51;
    border: 1px solid #F74C51;
    border-radius: 0.25rem;

    font-family: 'Staatliches', sans-serif;
    font-size: 1.5rem; //24px 
    line-height: 1.25rem; 
    letter-spacing: 2px;
    color: #fff;

    //margin: 2%;
`;
