import React, { useState } from 'react';
import { useCalendarContext } from '../../context/DataContext';

const TaskWeekly = () => {
    const [currentIndex, setCurrentIndex] = useState(0);
    const { listDataSelecionada } = useCalendarContext();

    const goToPrevious = () => {
        setCurrentIndex((prevIndex) => (prevIndex > 0 ? prevIndex - 1 : 0));
    };

    const goToNext = () => {
        setCurrentIndex((prevIndex) =>
            prevIndex < listDataSelecionada.length - 1 ? prevIndex + 1 : prevIndex
        );
    };

    return (
        <>
            <div className="carousel">
                {listDataSelecionada && (
                    <>
                        <button onClick={goToPrevious} disabled={currentIndex === 0}>
                            Anterior
                        </button>
                        <button onClick={goToNext} disabled={currentIndex === listDataSelecionada.length - 1}>
                            Pr√≥ximo
                        </button>
                    </>
                )}
                <div className="carousel-items">
                    {/* Renderizar o item atual */}
                    <div>{listDataSelecionada[currentIndex]}</div>
                </div>
            </div>
        </>
    )
}

export default TaskWeekly
