import React, { createContext, useContext, useState } from 'react';

// --------- Contexto global: Data selecionada ---------------
// Contexto para controlar a visibilidade do componente
const DataContext = createContext();

// Hook personalizado para acessar o contexto de visibilidade
export const useDataContext = () => {
    return useContext(DataContext);
};

// Componente de contexto que manter치 o dado
export const DataProvider = ({ children }) => {
    const [dataSelecionada, setDataSelecionada] = useState(null);

    const selecionarData = (data) => {
        setDataSelecionada(data);
    };

    return (
        <DataContext.Provider value={{ dataSelecionada, selecionarData }}>
            {children}
        </DataContext.Provider>
    );
};

// ------- Contexto global de controle do component TaskInfo ----------
// Contexto para controlar a visibilidade do componente
const ComponentVisibilityContext = createContext();

// Hook personalizado para acessar o contexto de visibilidade
export const useComponentVisibility = () => {
    return useContext(ComponentVisibilityContext);
};

// Componente de contexto que manter치 o estado da visibilidade
export const ComponentVisibilityProvider = ({ children }) => {
    const [isComponentVisible, setIsComponentVisible] = useState(false);

    const toggleComponentVisibility = () => {
        setIsComponentVisible(prevState => !prevState);
    };

    return (
        <ComponentVisibilityContext.Provider value={{ isComponentVisible, toggleComponentVisibility }}>
            {children}
        </ComponentVisibilityContext.Provider>
    );
};
